INSERT INTO `posts` (`id`, `name`, `alias`, `image`, `fk_catid`, `fk_catname`, `fk_parentid`, `view`, `IsCustomer`, `order`, `description`, `content`, `meta_title`, `meta_description`, `meta_keywords`, `status`, `create_at`, `update_at`) VALUES
(84, '“Đất nước chúng tôi không bao giờ dạy người lính đầu hàng”', 'd-t-nu-c-chung-toi-khong-bao-gi-d-y-ngu-i-linh-d-u-hang', 'upload/backup/truong sa 143.jpg', 19, NULL, NULL, 17, 0, 0, '<h2 class="fon33 mt1" style="margin: 5px 0px 0px; padding: 0px; font-weight: bold; font-size: 16px; line-height: 18px; font-family: ''Times New Roman''; color: #5f5f5f; text-align: justify;">(Dân trí) - “Tôi đang bị thương, trôi trên biển, tàu quân sự Trung Quốc phát hiện, chĩa súng vào đầu ra lệnh tôi đầu hàng. Tôi cương quyết không chịu. Nhìn thẳng vào mắt họ, tôi nói: Đất nước chúng tôi không bao giờ dạy người lính đầu hàng trước mũi súng quân thù”.</h2>\r\n', '\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Đó là dòng ký ức của binh nhất Trần Thiên Phụng, 1 trong 9 chiến sĩ của Trung đoàn 83 thuộc Bộ Tư lệnh Hải quân may mắn còn sống sót sau trận tử chiến ngày 14/3/1988 trên đảo Gạc Ma và Cô Lin thuộc quần đảo Trường Sa. Hiện anh sống cùng gia đình tại phường 2, TP Đông Hà, tỉnh Quảng Trị.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"><strong style="margin: 0px; padding: 0px;">Quyết chiến đấu đến hơi thở cuối cùng</strong></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;">25 năm đã trôi qua nhưng ký ức về trận chiến giữ đảo quê hương vẫn vẹn nguyên trong lòng người lính Trần Thiên Phụng. Thắp nén hương cho những đồng đội đã hy sinh, anh nghẹn ngào kể: Năm 23 tuổi, anh vào quân đội, làm chiến sĩ của Trung đoàn 83 thuộc Bộ Tư lệnh Hải quân, có nhiệm vụ thiêng liêng là bảo vệ đảo Gạc Ma thuộc quần đảo Trường Sa.</div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;"> </div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;" align="center"><img src="http://dantri4.vcmedia.vn/I3KdHJtU0B3ELPKGaTLe/Image/2013/03/gacma-200d2.jpg" border="0" alt="Anh Trần Thiên Phụng nhớ lại trận hải chiến sinh tử giữ đảo quê hương" style="margin: 0px; padding: 0px;" /><br style="margin: 0px; padding: 0px;" /><span style="margin: 0px; padding: 0px; font-family: Tahoma; font-size: 10pt;">Anh Trần Thiên Phụng nhớ lại trận hải chiến sinh tử giữ đảo quê hương</span></div>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Ngày 12/3/1988, lính Trung đoàn 83 lên ba chiếc thuyền đi làm nhiệm vụ xây dựng và bảo vệ chủ quyền ở Trường Sa. Đến chiều 13/3, ba tàu của ta đã tiến gần đảo Gạc Ma và đảo Cô Lin thì bất ngờ bị tàu quân sự phía Trung Quốc bao vây. Anh Nguyễn Thông (đảo trưởng) phát tín hiệu về đất liền xin ý kiến chỉ đạo. Khi đó, binh nhất Trần Thiên Phụng đang trên tàu vận chuyển HQ-604 hướng mũi vào đảo Gạc Ma cùng với tàu HQ-605 và tàu HQ-505. Phía tàu đối phương vẫn chạy theo bao vây uy hiếp.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Sáng ngày 14/3, trận hải chiến Trường Sa xảy ra. Tàu quân sự Trung Quốc một mặt cho quân đổ bộ vào đảo Gạc Ma, mặt khác tập trung hỏa lực tấn công vào tàu của ta. Ngay lập tức, anh Phụng cùng đồng đội trên tàu ôm súng AK 47 đứng trước mũi tàu bắn trả tàu của địch.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Tương quan lực lượng quá chênh lệch, nhiều chiến sĩ bên ta hy sinh. Tàu HQ-604 và tàu HQ-605 bị trúng hỏa lực mạnh của địch và dần chìm xuống biển. Còn tàu HQ-505 bị hư hại nặng.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Không nao núng, đảo trưởng Nguyễn Thông hô to: “Lãnh thổ của đất nước đã bị xâm chiếm, anh em xông lên quyết tâm đẩy lùi địch”. Nghe đảo trưởng hô, toàn bộ chiến sĩ ôm súng lao xuống biển, bơi vào đảo Gạc Ma tiếp tục nổ súng chiến đấu.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Khi đó, anh Phụng đã bị trúng 2 mảnh đạn vào đầu và tay nhưng vẫn cắn răng chịu đựng ôm súng bơi vào đảo, tiếp tục chiến đấu. Nhưng do bị thương nặng, mất máu nhiều nên anh Phụng đã kiệt sức và trôi lênh đênh trên biển. Chưa tiếp cận được đảo thì 17 giờ cùng ngày (14/3), anh Phụng bị tàu quân sự Trung Quốc phát hiện bắt giữ làm tù binh.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;">Nghe chồng kể tới đây, chị Lê Thị Thiên (vợ anh Phụng) cầm tay anh nghẹn ngào: “May mắn anh còn sống về đoàn tụ với gia đình”.</div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;"> </div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;" align="center"><img src="http://dantri4.vcmedia.vn/I3KdHJtU0B3ELPKGaTLe/Image/2013/03/gacma2-200d2.jpg" border="0" alt="Vợ chồng anh Phụng (ở giữa, hàng sau) trong ngày gặp lại ở Quảng Ninh năm 1991" style="margin: 0px; padding: 0px;" /></div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;" align="center"><span style="margin: 0px; padding: 0px; font-family: Tahoma; font-size: 10pt;">Vợ chồng anh Phụng (ở giữa, hàng sau) trong ngày gặp lại ở Quảng Ninh năm 1991</span></div>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Anh Phụng tiếp tục dòng ký ức: “Khi tôi đang bị thương trôi trên biển, tàu quân sự Trung Quốc phát hiện, họ chĩa súng vào đầu ra lệnh đầu hàng, tôi cương quyết không chịu. Nhìn thẳng vào mắt họ, tôi nói: Đất nước chúng tôi không bao giờ dạy người lính đầu hàng trước mũi súng quân thù”.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Sau đó, anh Phụng được đưa về đảo Hải Nam cấp cứu trước khi chuyển vào nhà tù ở Trạm Giang, tỉnh Quảng Đông, Trung Quốc. Hôm đó lính hải quân Việt Nam có tất cả 9 người bị bắt giữ.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"><strong style="margin: 0px; padding: 0px;">Ngày trùng phùng đẫm nước mắt</strong></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;">Ngày 28/3/1988, báo <em style="margin: 0px; padding: 0px;">Nhân dân </em>đăng tải danh sách 74 chiến sĩ mất tích ở trận tử chiến giữ chủ quyền Tổ quốc ngày 14/3/1988 trên đảo Gạc Ma và Cô Lin thuộc quần đảo Trường Sa. Anh Phụng là một trong số đó.</div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;"> </div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;" align="center"><img src="http://dantri4.vcmedia.vn/I3KdHJtU0B3ELPKGaTLe/Image/2013/03/gacma1-200d2.jpg" border="0" alt="Tấm giấy báo tử gửi về quê nhà" style="margin: 0px; padding: 0px;" /></div>\r\n<div class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; color: #000000; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px;" align="center"><span style="margin: 0px; padding: 0px; font-family: Tahoma; font-size: 10pt;">Tấm giấy báo tử gửi về quê nhà</span></div>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Ngày 1/11/1988, Trung đoàn 83 gửi giấy báo tử xác nhận anh Trần Thiên Phụng, cấp bậc binh nhất, mất tích vào ngày 14/3/1988 tại đảo Gạc Ma.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Đau đớn tột cùng, chị Thiên xót xa thương đứa con trong bụng chưa ra đời đã phải mồ côi cha. Bất ngờ hơn một năm sau, chị nhận được dòng tin nhắn của chồng gửi về từ Trung Quốc: <em style="margin: 0px; padding: 0px;">“Anh bị Trung Quốc bắt làm tù binh, em và bố mẹ giữ gìn sức khỏe”.</em> Đọc những dòng tin do Hội Chữ thập đỏ quốc tế gửi tới, chị không tin vào mắt mình.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Ngày 2/9/1991, anh Phụng cùng 8 đồng đội được phía Trung Quốc trao trả về Việt Nam qua cửa khẩu Hữu Nghị Quan ở Lạng Sơn. Chị Thiên bắt xe lên tận cửa khẩu đón chồng. Giây phút trùng phùng xúc động ấy, hai vợ chồng chị cứ ôm riết lấy nhau mà khóc cả giờ đồng hồ, không ai nói lên lời.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Trở về với cuộc sống đời thường, anh Phụng cùng vợ mở một quán ăn nhỏ bán bún, phở mưu sinh. Hiện giờ anh chị có 3 người con, đều đã khôn lớn.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;">Anh Phụng cho biết, ngày 14/3, anh vào Đà Nẵng để cùng với những đồng đội dự lễ tưởng niệm 25 năm hải chiến Trường Sa.</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000;"> </p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000; text-align: right;" align="right"><strong style="margin: 0px; padding: 0px;">Nguyễn Tuấn</strong></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 20px; color: #000000; text-align: right;" align="right"><strong style="margin: 0px; padding: 0px;">dantri.com.vn</strong></p>', NULL, NULL, NULL, 1, '2013-03-14 10:20:57', NULL),
(85, 'Cái “lưỡi bò” không gặm được nước Nam', 'dan-tri-l-ch-s-dan-t-c-vi-t-nam-da-ch-ng-minh-khi-co-du-c-long-ngu-i-t-p-h-p-du-c-nhan-dan-thi-cai-lu-i-bo-phuong-b-c-khong-g-m-du-c-du-ch-m-t-c-ng-c-nu-c-nam', 'upload/backup/hsts-85c28.jpg', 19, NULL, NULL, 13, 0, 0, '<p><span style="color: #5f5f5f; font-family: ''Times New Roman''; font-size: 15px; font-weight: bold; line-height: normal; text-align: justify;">(Dân trí) - Lịch sử dân tộc Việt Nam đã chứng minh, khi có được lòng người, tập hợp được nhân dân thì cái “lưỡi bò” phương Bắc không gặm được dù chỉ một “cọng cỏ” nước Nam.</span></p>\r\n', '\r\n<p style="text-align: justify;"><span style="color: #5f5f5f; font-family: ''Times New Roman''; font-size: 15px; font-weight: bold; line-height: normal; text-align: justify;"> </span></p>\r\n<p> </p>\r\n<p style="margin: 0px 0px 4.5px; padding: 0px; font-size: 16px; line-height: 20px; color: #000000; font-weight: 500;" align="center"><img src="http://dantri4.vcmedia.vn/x6yccccccccccccgLlrJ/Image/2013/01/hsts-85c28.jpg" border="0" alt="Cái “lưỡi bò” không gặm được nước Nam" width="400" align="center" style="margin: 5px auto; padding: 0px; display: block;" /></p>\r\n<div class="MsoNormal" style="margin: 0in 0in 0pt; padding: 0px; color: #000000; font-size: 16px; font-weight: 500; line-height: 20px; text-align: justify;"> <span style="font-family: ''Times New Roman'', serif; font-size: 10pt; line-height: 13.6pt;">Tham vọng thôn tính lãnh thổ của các quốc gia khác bằng cái lưỡi bò mà Trung Quốc tưởng tượng ra được thực hiện bằng nhiều mưu sâu chước độc. Cái lưỡi bò đó trơ tráo trên tấm hộ chiếu, trên bản đồ và các loại ấn phẩm văn hóa do Trung Quốc sản xuất. Cùng với cách tuyên truyền đó, họ còn cho lưỡi bò len lõi vào trong các sản phẩm công nghệ phần mềm hòng đầu độc não trạng người dùng.</span></div>\r\n<div class="MsoNormal" style="margin: 0in 0in 0pt; padding: 0px; color: #000000; font-size: 16px; font-weight: 500; line-height: 20px; text-align: justify;"><span style="font-family: ''Times New Roman'', serif; font-size: 10pt; line-height: 13.6pt;">Nhưng họ quên rằng, người Việt Nam trong và ngoài nước thừa biết các âm mưu thâm độc của họ. Lịch sử mấy ngàn năm của Đại Việt đủ sức để răn dạy mỗi công dân Việt Nam hôm nay biết cách bảo vệ chủ quyền quốc gia trước hiểm họa phương Bắc. Những mưu mô xâm lược dù tinh vi đến mấy, có thể che mờ mắt những ai đó, nhưng không thể qua mặt được nhân dân. Chính vì vậy, cho dù một nhà xuất bản sơ suất phát hành sách dạy học cho trẻ em có in cờ Trung Quốc hay bản đồ có đường lưỡi bò thì người dân cũng vạch ra và vứt sọt rác. Cho dù ngành du lịch  đãng trí giới thiệu tượng Phật của Trung Quốc tại một triển lãm tận bên Đức thì dân Việt vẫn phát hiện ra và  phản đối. Cho dù những tờ lịch và sổ tay đi từ đường Đài Loan sang Đà Nẵng có in hình lưỡi bò thì người dùng cũng biết để xử. Chính vì vậy, nên mới có những thanh niên Việt Nam âm thầm nhiều năm mua tên miền Tây Sa - Nam Sa (Xisha - Nansha) và Tam Sa (Sansha) mà Trung Quốc cho rằng của họ để bảo vệ chủ quyền Việt Nam trên mạng. Khi có kẻ “lạ” đòi mua lại tên miền, bạn Trần Duy Nguyễn (người đã mua những tên miền này) trả lời chắc nịch: “Tên miền này  không phải để bán, tôi giữ nó để bảo vệ chủ quyền đất nước”.</span></div>\r\n<div class="MsoNormal" style="margin: 0in 0in 0pt; padding: 0px; color: #000000; font-size: 16px; font-weight: 500; line-height: 20px; text-align: justify;"><span style="font-family: ''Times New Roman'', serif; font-size: 10pt; line-height: 13.6pt;">Mới đây,  WeChat - ứng dụng nhắn tin và gọi điện miễn phí trên Internet của Tập đoàn Tencent (Trung Quốc) đưa bản đồ lưỡi bò hòng lập lờ tuyên truyền chủ quyền lãnh thổ Trung Quốc trên biển Đông. Hậu quả là chỉ trong mấy ngày, WeChat nhận lãnh là bị tụt hạng từ tốp 3 xuống thứ 13 trong nhóm ứng dụng “liên lạc miễn phí hàng đầu”.</span></div>\r\n<div class="MsoNormal" style="margin: 0in 0in 0pt; padding: 0px; color: #000000; font-size: 16px; font-weight: 500; line-height: 20px; text-align: justify;"><span style="line-height: 13.6pt; font-size: 10pt; font-family: ''Times New Roman'', serif;">Chưa hết, theo dự báo nó sẽ còn rớt hạng thê thảm vì bị người dùng Việt tẩy chay.</span><span style="line-height: 13.6pt; font-size: 10pt; font-family: ''Times New Roman'', serif; color: #333333;"> </span><span style="line-height: 13.6pt; font-size: 10pt; font-family: ''Times New Roman'', serif;">Trên “mặt trận” này, không cần ai lãnh đạo, không cần có chủ trương, từng cá nhân tự nhận thức và quyết tâm bẻ gãy ý đồ xâm lược của ngoại bang.</span></div>\r\n<div class="MsoNormal" style="margin: 0in 0in 0pt; padding: 0px; color: #000000; font-size: 16px; font-weight: 500; line-height: 20px; text-align: justify;">\r\n<p class="MsoNormal" style="margin-bottom: 0.0001pt; line-height: 13.6pt;"><span style="font-size: 10pt; font-family: ''Times New Roman'', serif;"> </span><span style="color: #333333; font-family: ''Times New Roman'', serif; font-size: 10pt; line-height: 13.6pt;">Nhận thức về bảo vệ chủ quyền và tinh thần cảnh giác của người dân rất cao. Và từ bao  đời, bảo vệ đất nước là xương máu và trí tuệ của nhân dân. Bảo vệ chủ quyền quốc gia hôm nay cũng vậy, chỉ bằng sức mạnh và trí tuệ của nhân dân.</span></p>\r\n<p class="MsoNormal" style="margin-bottom: 0.0001pt; line-height: 13.6pt;"><span style="font-size: 10pt; font-family: ''Times New Roman'', serif;"> </span><span style="color: #333333; font-family: ''Times New Roman'', serif; font-size: 10pt; line-height: 13.6pt;">Đặc biệt đối với giới trẻ, kiến thức, trí tuệ cùng với lòng yêu nước tự nhiên trong mỗi người đã giúp họ luôn luôn cảnh giác để từ đó công khai phản đối và chống lại hành động xâm lược, phi nghĩa và trái pháp luật quốc tế của Trung Quốc. Cái lưỡi bò từ phương Bắc dù bằng rất nhiều cách, cũng không thể thực hiện được dã tâm xâm lược.</span></p>\r\n<p class="MsoNormal" style="margin-bottom: 0.0001pt; line-height: 13.6pt;"><span style="font-size: 10pt; font-family: ''Times New Roman'', serif;"> </span><span style="font-family: ''Times New Roman'', serif; font-size: 10pt; line-height: 13.6pt;">Lịch sử dân tộc Việt Nam đã chứng minh, khi có được lòng người, tập hợp được nhân dân thì cái “lưỡi bò” phương Bắc không gặm được dù chỉ một “cọng cỏ” nước Nam.</span></p>\r\n<p class="MsoNormal" style="line-height: 13.6pt;"><strong><span style="font-size: 10.0pt; font-family: ''Times New Roman'',''serif''; mso-fareast-font-family: ''Times New Roman''; color: #333333;">Lê Chân Nhân</span></strong></p>\r\n</div>\r\n<p> </p>', NULL, NULL, NULL, 1, '2013-03-18 02:26:37', NULL),
